var timeFormat = {
  hour: 'numeric',
  minute: 'numeric'
};
export function timeUntil(now, then, full) {
  var diff = then.getTime() - now.getTime();

  if (diff < -900000) {
    return 'en route';
  } else if (diff < 0) {
    return 'boarding now';
  } else {
    var hours = Math.floor(diff / 3600000);
    var minutes = Math.floor(diff % 3600000 / 60000);

    if (hours < 1) {
      return full ? "next is in ".concat(minutes, " minutes at ").concat(then.toLocaleString(undefined, timeFormat)) : "".concat(minutes, " minutes");
    } else {
      return full ? "next is in ".concat(hours, " hours at ").concat(then.toLocaleString(undefined, timeFormat)) : "".concat(hours, " hours");
    }
  }
}
