var timeFormat = {
  hour: 'numeric',
  minute: 'numeric'
};
export function timeUntil(now, then, full) {
  var diff = then.getTime() - now.getTime();

  if (diff < -900000) {
    return 'en route';
  } else if (diff < 0) {
    return 'boarding now';
  } else {
    var hours = Math.floor(diff / 3600000);
    var minutes = Math.floor(diff % 3600000 / 60000);

    if (hours < 1) {
      return full ? "next is in ".concat(minutes, " ").concat(minutes > 1 ? 'minutes' : 'minute', " at ").concat(then.toLocaleString(undefined, timeFormat)) : "in ".concat(minutes, " ").concat(minutes > 1 ? 'minutes' : 'minute');
    } else {
      if (minutes >= 30) {
        ++hours;
      }

      return full ? "next is in ".concat(hours, " ").concat(hours > 1 ? 'hours' : 'hour', " at ").concat(then.toLocaleString(undefined, timeFormat)) : "in ".concat(hours, " ").concat(hours > 1 ? 'hours' : 'hour');
    }
  }
}
